import React, { useState } from 'react';
import './App.css';  // Import the CSS file

function App() {
  const [items, setItems] = useState(0);
  const [packs, setPacks] = useState([]);
  const [summary, setSummary] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8080/order', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ items }),
      });
      const data = await response.json();
      setPacks(data.packs);
      setSummary(generateSummary(data.packs));
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const generateSummary = (packs) => {
    return packs.map(pack => `${pack.Count}*${pack.Size}`).join(' + ');
  };

  return (
    <div className="container">
      <img src="/gymshark-logo.png" alt="Gymshark Logo" className="logo" />
      <h1>Gymshark Pack Calculator</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Items:
          <input
            type="number"
            value={items}
            onChange={(e) => setItems(Number(e.target.value))}
            className="input"
          />
        </label>
        <button type="submit" className="button">Calculate Packs</button>
      </form>
      <h2>Calculated Packs:</h2>
      <ul className="packs-list">
        {packs.map((pack, index) => (
          <li key={index} className="pack-item">Pack Size: {pack.Size} (Count: {pack.Count})</li>
        ))}
      </ul>
      {summary && (
        <div className="summary">
          <h2>Summary:</h2>
          <p>{summary}</p>
        </div>
      )}
    </div>
  );
}

export default App;
